import React, { Component } from 'react';
import './index.css';

import validate from './validate';
import PhoneNumber from './PhoneNumber';
import AccessCode from './AccessCode';


class App extends Component {
  constructor() {
    super();

    this.state = {
      formIsValid: false,
      formControls: {

        phone_number: {
          value: '',
          placeholder: 'Phone number',
          valid: false,
          validationRules: {
            maxLength: 10,
            isRequired: true
          },
          touched: false
        },
        access_code: {
          value: '',
          placeholder: 'Access Code',
          valid: false,
          validationRules: {
            isRequired: true,
            maxLength: 6,
          },
          touched: false
        },

      },
    }
  }


  changeHandler = event => {

      const name = event.target.name;
      const value = event.target.value;

      const updatedControls = {
        ...this.state.formControls
      };
      const updatedFormElement = {
        ...updatedControls[name]
      };
      updatedFormElement.value = value;
      updatedFormElement.touched = true;
      updatedFormElement.valid = validate(value, updatedFormElement.validationRules);

      updatedControls[name] = updatedFormElement;

      let formIsValid = true;
      for (let inputIdentifier in updatedControls) {
        formIsValid = updatedControls[inputIdentifier].valid && formIsValid;
      }

      this.setState({
        formControls: updatedControls,
        formIsValid: formIsValid
      });

  }


  formSubmitHandler = () => {
    const formData = {};
    for (let formElementId in this.state.formControls) {
        formData[formElementId] = this.state.formControls[formElementId].value;
    }

        console.dir(formData);
    }

  // code to check express connection
  callAPI() {
    fetch("http://localhost:9000/testAPI")
        .then(res => res.text())
        .then(res => this.setState({ apiResponse: res }));
  }

  componentWillMount() {
    this.callAPI();
  }

  onSubmit = (e) => {
    e.preventDefault();
    // get form data out of state
    const { phone_number, access_code } = this.state;

    fetch('http://localhost:9000/api/users/' , {
      method: "POST",
      headers: {
        'Content-type': 'application/json'
      }
      .then((result) => {
        console.log(result)
      })
    })
  }


  render() {

    return (
      <div className="App">
          <header>
            <h1> Skipli Coding Challenge - Tung </h1>
            <p className="App-intro">;{this.state.apiResponse}</p>
          </header>

      <div className = "content"> 
          <PhoneNumber name="phone_number" 
                     placeholder={this.state.formControls.phone_number.placeholder}
                     value={this.state.formControls.phone_number.value}
                     onChange={this.changeHandler}
                     touched={this.state.formControls.phone_number.touched}
                     valid={this.state.formControls.phone_number.valid}
          />
          <AccessCode name="access_code"
                  placeholder={this.state.formControls.access_code.placeholder}
                  value={this.state.formControls.access_code.value}
                  onChange={this.changeHandler}
                  touched={this.state.formControls.access_code.touched}
                  valid={this.state.formControls.access_code.valid}
          />
          <button onClick={this.formSubmitHandler} 
                  disabled={! this.state.formIsValid}
            > 
              Submit
          </button>
      </div>
    </div>
    );

  }
}

export default App;
