  handleSubmit = e=>{
    const data = {phone: this.state.formControls.phone.value,
    code: this.state.formControls.code.value,};
    const todoRef = firebase.database().ref('users');
    todoRef.push(data)
  }


  render() {

    return (
      <div className="App">
          <header>
            <h1> Skipli Coding Challenge - Tung </h1>
            <p className="App-intro">;{this.state.apiResponse}</p>
          </header>

      <div className = "content"> 
        <form onSubmit={this.handleSubmit}>
            <label>Enter the Name</label>
            <PhoneNumber name="phone" 
                      placeholder={this.state.formControls.phone.placeholder}
                      value={this.state.formControls.phone.value}
                      onChange={this.changeHandler}
                      touched={this.state.formControls.phone.touched}
                      valid={this.state.formControls.phone.valid}
            />

            <br/>
            <label>Enter the Code</label>
            <AccessCode name="code"
                    placeholder={this.state.formControls.code.placeholder}
                    value={this.state.formControls.code.value}
                    onChange={this.changeHandler}
                    touched={this.state.formControls.code.touched}
                    valid={this.state.formControls.code.valid}
            />
            <br/>
            <button onClick={this.handleSubmit} 
                    disabled={!this.state.formIsValid}
              > 
                Submit
            </button>
            <br/>
            </form>

      </div>
    </div>
    );

  }
}

export default App;
