import React, { Component } from 'react';
import axios from 'axios';

import './index.css';

import validate from './validate';
import PhoneNumber from './PhoneNumber';
import AccessCode from './AccessCode';


class App extends Component {
  constructor() {
    super();

    this.state = {
      formIsValid: false,
      formControls: {

        phone: {
          value: '',
          placeholder: 'Phone number',
          valid: "false",
          validationRules: {
            maxLength: 10,
            isRequired: true
          },
          touched: "false"
        },
        code: {
          value: '',
          placeholder: 'Access Code',
          valid: "false",
          validationRules: {
            isRequired: false,
            maxLength: 6,
          },
          touched: "false"
        },

      },
    }
  }


  changeHandler = event => {

      const name = event.target.name;
      const value = event.target.value;

      const updatedControls = {
        ...this.state.formControls
      };
      const updatedFormElement = {
        ...updatedControls[name]
      };
      updatedFormElement.value = value;
      updatedFormElement.touched = true;
      updatedFormElement.valid = validate(value, updatedFormElement.validationRules);

      updatedControls[name] = updatedFormElement;

      let formIsValid = true;
      // console.log(updatedControls);
      for (let inputIdentifier in  updatedControls){
        formIsValid = updatedControls[inputIdentifier].valid && formIsValid;
      }
      // formIsValid = updatedControls["phone"].valid && formIsValid;
      this.setState({
        formControls: updatedControls,
        formIsValid: formIsValid
      });

  }


  formSubmitHandler = () => {
    const formData = {};
    for (let formElementId in this.state.formControls) {
        formData[formElementId] = this.state.formControls[formElementId].value;
    }
        console.dir(formData);
    }

  // code to check express connection
  callAPI() {
    fetch("http://localhost:9000/testAPI")
        .then(res => res.text())
        .then(res => this.setState({ apiResponse: res }));
  }

  componentWereMount() {
    this.callAPI();
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }



  handleSubmit = (event) => {
    alert('A form was submitted: ' + this.state + this.state.formControls.code.value);
    var data = {
      phone : this.state.formControls.phone.value,
      code: this.state.formControls.code.value
    }
    fetch('http://localhost:9000/users/adduser', {
        method: 'POST',
        // We convert the React state to JSON and send it as the POST body
        body: JSON.stringify(data)
      }).then(function(response) {
        console.log(response)
        return response.json();
      });

    event.preventDefault();
  }

  render() {

    return (
      <div className="App">
          <header>
            <h1> Skipli Coding Challenge - Tung </h1>
            <p className="App-intro">;{this.state.apiResponse}</p>
          </header>

      <div className = "content"> 
        <form onSubmit={this.handleSubmit}>
            <label>Enter the Name</label>
            <PhoneNumber name="phone" 
                      placeholder={this.state.formControls.phone.placeholder}
                      value={this.state.formControls.phone.value}
                      onChange={this.changeHandler}
                      // touched={this.state.formControls.phone.touched}
                      // valid={this.state.formControls.phone.valid}
            />

            <br/>
            <label>Enter the Code</label>
            <AccessCode name="code"
                    placeholder={this.state.formControls.code.placeholder}
                    value={this.state.formControls.code.value}
                    onChange={this.changeHandler}
                    // touched={this.state.formControls.code.touched}
                    // valid={this.state.formControls.code.valid}
            />
            <br/>
            <button onClick={this.formSubmitHandler} 
                    disabled={!this.state.formIsValid}
              > 
                Submit
            </button>
            <br/>
            </form>

      </div>
    </div>
    );

  }
}

export default App;
